pragma solidity 0.8.6;

interface ICarbonX {
    function isTransferIntoVaultAccepted(
        address operator,
        address from,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        bytes memory data
    ) external view returns (bool);

    function onTransferIntoVaultSuccessfullyDone(
        address operator,
        address from,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        bytes memory data
    ) external returns (bytes4);
}
